import React from 'react';

class AboutMe extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            hero: {
                name: '',
                height: '',
                birth_year: '',
                mass: '',
                skin_color: '',
                eye_color: ''
            }
        }
    }

    componentDidMount() {
        let hero = localStorage.getItem('hero');
        hero = JSON.parse(hero);
        if (hero) {
            this.setState({
                name : hero.name,
                height: hero.height,
                birth_year: hero.birth_year,
                gender: hero.gender,
                mass: hero.mass,
                hair_color: hero.hair_color,
                skin_color: hero.skin_color

            })
        } else {
            fetch('https://sw-info-api.herokuapp.com/v1/peoples/1')
                .then(response => response.json())
                .then(data => {
                    this.setState(this.state.hero = {
                        name: data.name,
                        height: data.height,
                        birth_year: data.birth_year,
                        gender: data.gender,
                        mass: data.mass,
                        hair_color: data.hair_color,
                        skin_color: data.skin_color
                    });
                    localStorage.setItem('hero', JSON.stringify(this.state.hero));
                })
        }
    }

    render() {
        const text = this.state.isLoading ? 'Loading' : '';
        return (
            <div>
                <p>Name: {text}{this.state.name}</p>
                <p>Height: {text}{this.state.height}</p>
                <p>Birth date: {text}{this.state.birth_year}</p>
                <p>Gender: {text}{this.state.gender}</p>
                <p>Mass: {text}{this.state.mass}</p>
                <p>Hair color: {text}{this.state.hair_color}</p>
                <p>Skin color: {text}{this.state.skin_color}</p>
                <p>eye color: {text + this.state.eye_color}</p>
            </div>
        );
    }

};

export default AboutMe;
