import React from 'react';

class AboutMe extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            hero: {
                name: '',
                height: '',
                birth_year: '',
                mass: '',
                skin_color: '',
                eye_color: ''
            }
        }
    }

    componentDidMount() {
        let hero = localStorage.getItem('hero');
        hero = JSON.parse(hero);
        console.log(hero)
        if(hero === null) {
            fetch('https://sw-info-api.herokuapp.com/v1/peoples/1')
                .then(response => response.json())
                .then(data => {
                    this.setState(this.state.hero = {
                        name: data.name,
                        height: data.height,
                        birth_year: data.birth_year,
                        gender: data.gender,
                        mass: data.mass,
                        hair_color: data.hair_color,
                        skin_color: data.skin_color
                    });
                    localStorage.setItem('hero', JSON.stringify(this.state.hero));
                })
        }
    }

    render() {
        return (
            <div>
                <p>Name: {this.state.name}</p>
                <p>Height: {this.state.height}</p>
                <p>Birth date: {this.state.birth_year}</p>
                <p>Gender: {this.state.gender}</p>
                <p>Mass: {this.state.mass}</p>
                <p>Hair color: {this.state.hair_color}</p>
                <p>Skin color: {this.state.skin_color}</p>
                <p>eye color: {this.state.eye_color}</p>
            </div>
        );
    }

};

export default AboutMe;
